export interface Dict<V> {
    [key: string]: V;
}
export declare type Literal = string | number | boolean | null | RegExp;
export interface Capture {
    name: string;
    filterList: Filter[];
    modifier: Modifier;
}
export interface Filter {
    isArrayFilter: boolean;
    name: string;
    args: Literal[];
}
export interface Modifier {
    name: string;
    args: Literal[];
}
export declare type TemmeSelector = ParentRefSelector | NormalSelector | Assignment | SnippetDefine | SnippetExpand | FilterDefine | ModifierDefine | ProcedureDefine;
export declare type ExpandedTemmeSelector = ParentRefSelector | NormalSelector | Assignment | SnippetDefine | FilterDefine | ModifierDefine | ProcedureDefine;
export interface NormalSelector {
    type: 'normal-selector';
    sections: Section[];
    procedure: Procedure;
    arrayCapture: Capture;
    children: TemmeSelector[];
}
export interface ParentRefSelector {
    type: 'parent-ref-selector';
    section: Section;
    procedure: Procedure;
}
export interface Procedure {
    name: string;
    args: (Literal | Capture)[];
}
export interface Assignment {
    type: 'assignment';
    capture: Capture;
    value: Literal;
}
export interface SnippetDefine {
    type: 'snippet-define';
    name: string;
    selectors: TemmeSelector[];
}
export interface SnippetExpand {
    type: 'snippet-expand';
    name: string;
}
export interface FilterDefine {
    type: 'filter-define';
    name: string;
    argsPart: string;
    code: string;
}
export interface ModifierDefine {
    type: 'modifier-define';
    name: string;
    argsPart: string;
    code: string;
}
export interface ProcedureDefine {
    type: 'procedure-define';
    name: string;
    argsPart: string;
    code: string;
}
export interface Section {
    combinator: Combinator;
    element: string;
    qualifiers: Qualifier[];
}
export declare type Combinator = ' ' | '>' | '+' | '~';
export declare type Qualifier = IdQualifier | ClassQualifier | AttributeQualifier | PseudoQualifier;
export interface IdQualifier {
    type: 'id-qualifier';
    id: string;
}
export interface ClassQualifier {
    type: 'class-qualifier';
    className: string;
}
export declare type AttributeOperator = '=' | '~=' | '|=' | '*=' | '^=' | '$=';
export interface AttributeQualifier {
    type: 'attribute-qualifier';
    attribute: string;
    operator: AttributeOperator;
    value: string | Capture;
}
export interface PseudoQualifier {
    type: 'pseudo-qualifier';
    name: string;
    content: string;
}
